.nav {
  // border-bottom: .8rem solid $mohogany;
  background-color: $yellow;
  text-transform: uppercase;
  position: fixed;
  top: 0;
  z-index: 1;
  width: 100%;
}

.nav-content {
  display: flex;
  flex-direction: column;
  height: 8rem;
  overflow: hidden;
}

@media (min-width: $laptop) {
  .nav-content {
    height: auto;
    padding-left: 8%;
    padding-right: 8%;
    // padding-top: 2rem;
  }
}

@media (min-width: $desktop) {
  .nav-content {
    max-width: 120rem;
    // max-width: 100rem;
    margin: auto;
  }

  #searchInput {
    max-width: 20rem;
  }
}

.dropDown-menu {
  // background-color: white;
  cursor: pointer;
  padding: 2.5rem;
  // padding-right: 0;
  position: absolute;
  width: 3rem;
  // right: 0;
  //   margin: 0 auto;
}

@media (min-width: $laptop) {
  .dropDown-menu {
    display: none;
  }
}

.dropDown-menu div {
  transition: 0.1s ease-in;
}

.dropDown-menu {
  .one,
  .two,
  .three {
    background-color: $mohogany;
    height: 0.3rem;
    margin-bottom: 0.7rem;
    width: 3rem;
  }
  // .two {
  //   background-color: $mohogany;
  //   height: 0.5rem;
  //   margin-bottom: 0.5rem;
  //   width: 3rem;
  // }
  // .three {
  //   background-color: $mohogany;
  //   height: 0.5rem;
  //   margin-bottom: 0.5rem;
  //   width: 3rem;
  // }
}

.dropDown-menu.open {
  .one {
    transform: translateY(1rem) rotate(45deg);
  }
  .two {
    opacity: 0;
  }
  .three {
    transform: translateY(-1rem) rotate(-45deg);
  }
}

.dropDown-menuContent {
  display: none;
}

.dropDown-menu.display {
  background-color: $lightYellow;
  height: 96.5vh;
  width: 100vw;
  transition: 0.3s;
  z-index: 2;
  overflow: hidden;
}

@media (min-width: $tablet) {
  .dropDown-menu.display {
    width: 35vw;
  }
}

@media (min-width: $laptop) {
  .dropDown-menu.display {
    width: 20vw;
  }
}

.dropDown-menu.display .dropDown-menuContent {
  display: grid;
  grid-template-columns: 1fr;
  grid-gap: 1.5rem;
}

.dropDown-menuContent {
  // width: 86vw;
  cursor: auto;
  margin: 0 auto;

  // height: 100vh;
  // align-items: center;
}

.dropDown-menuContent div {
  font-size: 2.5rem;
  width: 96.5vw;
  text-align: center;
  height: auto;
  background-color: inherit;
  font-family: $secondFont;
  font-weight: 500;
}

@media (min-width: $tablet) {
  .dropDown-menuContent div {
    width: 35vw;
