  }
}

.button {
  width: 20rem;
  font-family: $mainFont;
  font-size: 1.75rem;
  background-color: $red;
  color: $yellow;
  margin: 3rem auto 2rem;
  padding: 1rem;
  text-align: center;
  border-radius: 0.4rem;
  -moz-outline-radius: 0.4rem;
  font-style: normal;
}

.button:hover {
  // background-color: $yellow;
