@import "./variables";
@import "./about";
@import "./contact";
@import "./get_involved";
@import "./nav-footer";
@import "./projects-main";
@import "./projects";
@import "./publications";
@import "./publications_main";
@import "./home";
@import "./pagination";
// @import "../../node_modules/slick-carousel/slick/slick";
// @import "../../node_modules/slick-carousel/slick/slick-theme";

* {
  margin: 0;
  padding: 0;
}
html {
  font-size: 62.5%;
  padding: 0;
  margin: 0;
  box-sizing: border-box;
  width: 100%;
  scroll-behavior: smooth;
  // background-color: $lightCoral;
}

body {
  overflow-x: hidden !important;
}

::selection {
  background: $yellow;
}

a {
  color: inherit;
  text-decoration: none;
  outline: none !important;
}

a:hover {
  text-decoration: none;
}

.searchButton {
  cursor: pointer;
}

#searchInput {
  display: none;
  margin-left: 1.2%;
  margin-bottom: 1%;
}

#search-title {
  margin-top: 10%;
}

#search-header {
  color: #be5456;
  margin-bottom: 2%;
}

.search-results {
  width: 75%;
  margin-top: 1%;
  margin-bottom: 20%;
  margin-left: 20%;
  font-size: 2rem;
}

.search-result-container {
  cursor: pointer;
}

.search-result {
  background-color: #ffedc5;
  font-weight: bolder;
  margin-top: 1%;
  margin-bottom: 1%;
  color: #913132;
  font-family: "Playfair Display", serif !important;
  width: 70%;
  margin-left: 2.5%;
  padding: 1%;
  cursor: pointer;
}

.resultLine {
  width: 30%;
  margin-left: 5%;
}

